    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
        this.setIconImage(new javax.swing.ImageIcon(getClass().getClassLoader().getResource("oasis/OASIS Logo Sun.png")).getImage());
        setLocationRelativeTo(null);
        saveUsername.setFocusable(false);
        savePassword.setFocusable(false);
        saveUsername.setState(true);
        serversDialog.setLocationRelativeTo(this);
    }//GEN-LAST:event_formWindowOpened

    private void savePasswordItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_savePasswordItemStateChanged
        if(savePassword.getState()){
            saveUsername.setState(true);
        }
    }//GEN-LAST:event_savePasswordItemStateChanged

